mapper.select:
SELECT stno tpno, svno, qno, qbody body, DECODE(qupno, NULL, 0, qupno) qupno FROM survey, quest WHERE qno = sqno AND stno = #{tpno}
AND qupno IS NULL
AND qupno = #{qupno}
ORDER BY qno

mapper.insert:
INSERT INTO
    survey
VALUES(
    (SELECT NVL(MAX(svno) + 1, 1001) FROM survey),
    #{tpno}, #{qno}
)

INSERT INTO
    surveyanswer(sano, ssno, sa_id)
VAlUES(
    (SELECT NVL(MAX(sano)+1, 1001) FROM surveyanswer),
    (SELECT
        svno
    FROM
        survey
    WHERE
        sqno = #{qano}
        AND stno = #{tpno}
    ),
    #{id}
)
